 298	<script>
 299	// Mobile menu toggle functionality
 300	document.addEventListener('DOMContentLoaded', function() {
 301	 const mobileMenuToggle = document.querySelector('.mobile-menu-toggle');
 302	 const primaryNav = document.querySelector('.primary-navigation');
 303	
 304	 if (mobileMenuToggle && primaryNav) {
 305	 mobileMenuToggle.addEventListener('click', function() {
 306	 mobileMenuToggle.classList.toggle('active');
 307	 primaryNav.classList.toggle('active');
 308	 });
 309	
 310	 // Close menu when clicking on a link
 311	 const navLinks = primaryNav.querySelectorAll('a');
 312	 navLinks.forEach(link => {
 313	 link.addEventListener('click', function() {
 314	 mobileMenuToggle.classList.remove('active');
 315	 primaryNav.classList.remove('active');
 316	 });
 317	 });
 318	
 319	 // Close menu when clicking outside
 320	 document.addEventListener('click', function(event) {
 321	 if (!primaryNav.contains(event.target) && !mobileMenuToggle.contains(event.target)) {
 322	 mobileMenuToggle.classList.remove('active');
 323	 primaryNav.classList.remove('active');
 324	 }
 325	 });
 326	 }
 327	});
